import React from "react";
import "./App.css";

class Ejercicio extends React.Component {
  render() {
    return (
      <div>
        {"Ej"}
        <label>
          <input type="text" />
        </label>
        {"  "}
        <label>
          <input type="text" />
        </label>
        {" X "}
        <label>
          <input type="text" />
        </label>
        {"  "}
        <label>
          <input type="text" />
        </label>
        {" Kg "}
      </div>
    );
  }
}

class Mas extends React.Component {
  constructor(state) {
    super(state);
    this.state = {
      clickead: 0,
      ejercicios: []
    };
  }

  handleClick = () => {
    this.setState({ clickead: 1 });
    const aux = new Ejercicio();
    let ejs = this.state.ejercicios;
    ejs.push(aux);
    this.setState({ ejercicios: ejs });
  };
  render() {
    return (
      <div className="container">
        <button
          className="btn btn-primary m-2"
          aria-label="Left Align"
          onClick={this.handleClick}
        >
          +
        </button>
        {this.state.ejercicios.map(ejercicio => (
          <Ejercicio />
        ))}
      </div>
    );
  }
}

export default Mas;
