import React from "react";
import "./App.css";

class Ejercicio extends React.Component {
  render() {
    return (
      <div className="input-group m-3">
        <label>
          <input className="form-control" placeholder="Ejercicio" type="text" />
        </label>
        <label>
          <input className="form-control" placeholder="Series" type="text" />
        </label>
        <label>
          <input
            className="form-control"
            placeholder="Repeticiones"
            type="number"
          />
        </label>
        <label>
          <input className="form-control" placeholder="Kg" type="text" />
        </label>
      </div>
    );
  }
}

class Mas extends React.Component {
  constructor(state) {
    super(state);
    this.state = {
      clickead: 0,
      ejercicios: []
    };
  }

  handleClick = () => {
    this.setState({ clickead: 1 });
    const aux = new Ejercicio();
    let ejs = this.state.ejercicios;
    ejs.push(aux);
    this.setState({ ejercicios: ejs });
  };
  render() {
    return (
      <div className="container">
        <button
          className="btn btn-primary m-2"
          aria-label="Left Align"
          onClick={this.handleClick}
        >
          +
        </button>

        {this.state.ejercicios.map(ejercicio => (
          <Ejercicio />
        ))}
      </div>
    );
  }
}

export default Mas;
