import React from "react";
import "./App.css";

class Mas extends React.Component {
  constructor(state) {
    super(state);
    this.state = {
      clickead: 0,
      ejercicios: []
    };
  }

  handleDelete = () => {
    const ejercicio = this.state.ejercicios.filter(c => c.id !== ejercicios);
    this.setState({ ejercicios: ejercicios });
  };

  handleClick = () => {
    this.setState({ clickead: 1 });
    const aux = new Ejercicio();
    let ejs = this.state.ejercicios;
    ejs.push(aux);
    this.setState({ ejercicios: ejs });
  };
  render() {
    return (
      <div id="div-center" className="container">
        <button
          className="btn btn-primary m-2"
          aria-label="Left Align"
          onClick={this.handleClick}
        >
          +
        </button>

        {this.state.ejercicios.map(ejercicio => (
          <Ejercicio />
        ))}
      </div>
    );
  }
}

class Ejercicio extends React.Component {
  render() {
    return (
      <div className="input-group mb-3">
        <input
          id="ejer"
          className="form-control"
          placeholder="Ejercicio"
          type="text"
        />
        <input className="form-control" placeholder="Series" type="number" />
        <input className="form-control" placeholder="Repes" type="number" />
        <input className="form-control" placeholder="Kg" type="number" />
        <button onClick={this.handleDelete} class="btn btn-danger">
          <i class="fa fa-trash"></i>
        </button>
      </div>
    );
  }
}

export default Mas;
